security {
    macsec {
        connectivity-association {{ MACSEC['connectivity-association-name'] }} {
            cipher-suite {{ MACSEC['cipher-suite'] }};
            security-mode {{ MACSEC['security-mode'] }};
            include-sci;
            pre-shared-key {
                ckn {{ MACSEC['ckn'] }};
                cak "{{ MACSEC['cak'] }}";
            }
        }
    }
}
