forwarding-options {
    analyzer {
{% for Analyser in Port_Mirroring %}
            {{ Analyser['Analyser_Name'] }} {
                input {
                    ingress {
                            interface {{ Analyser['Input_Ingress_Intf'] }};
                    }
                    egress {
                            interface {{ Analyser['Input_Egress_Intf'] }};
                    }
                }
                output {
{% if Analyser['Analyser_Type'] == 'Local' %}
                        interface {{ Analyser['Output_Intf'] }};
{% elif Analyser['Analyser_Type'] == 'Remote' %}
                        ip-address {{ Analyser['Output_IP'] }};
{% endif %}
                }
            }
        }
{% endfor %}
    }
